  {title: "Wingspan", description: "Wingspan is a competitive, medium-weight, card-driven, engine-building board game from Stonemaier Games. It's designed by Elizabeth Hargrave and features over 170 birds illustrated by Natalia Rojas and Ana Maria Martinez.", number_of_players: 5, category_id: 1},
  {title: "Gloomhaven", description: "Gloomhaven is a game of Euro-inspired tactical combat in a persistent world of shifting motives. Players will take on the role of a wandering adventurer with their own special set of skills and their own reasons for traveling to this dark corner of the world.", number_of_players: 4, category_id: 2},
  {title: "Brass: Birmingham", description: "Brass Birmingham is an economic strategy game sequel to Martin Wallace 2007 masterpiece, Brass. Birmingham tells the story of competing entrepreneurs in Birmingham during the industrial revolution, between the years of 1770-1870.", number_of_players: 4, category_id: 2},
  {title: "Terraforming Mars", description: "In the 2400s, mankind begins to terraform the planet Mars. Giant corporations, sponsored by the World Government on Earth, initiate huge projects to raise the temperature, the oxygen level, and the ocean coverage until the environment is habitable.", number_of_players: 5, category_id: 3},
  {title: "Twilight Imperium (Fourth Edition)", description: "Twilight Imperium (Fourth Edition) is a game of galactic conquest in which three to six players take on the role of one of seventeen factions vying for galactic domination through military might, political maneuvering, and economic bargaining.", number_of_players: 6, category_id: 2},
  {title: "Ultimate Werewolf: Deluxe Edition", description: "One of the most popular social deductions games of all time, Ultimate Werewolf takes the familiar homebrewed rules of the game Mafia and refines them into addicting gameplay.", number_of_players: 20, category_id: 4},
